  model_conten = #{modelConten,jdbcType=VARCHAR},
  date = #{date,jdbcType=TIMESTAMP},
  describe = #{describe,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
