mapper.update:
update t_journey_plan
set customer_num = #{customerNum,jdbcType=INTEGER},
  prospectus = #{prospectus,jdbcType=VARCHAR},
  status = #{status,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, customer_num, prospectus, status
from t_journey_plan
where id = #{id,jdbcType=INTEGER}
